import React from "react";
import axios from "axios";
class App extends React.Component {
  state = {
    advice: "",
  };
  componentDidMount() {
    this.fetchData();
  }
  fetchData = () => {
    axios
      .get(`https://api.adviceslip.com/advice`)
      .then((response) => {
        console.log(response);
      })
      .catch((error) => {
        console.log(error);
      });
  };
  render
}
export default App;
